TASKS.AGENT_CODE:

#include <stdio.h>
#include <time.h>
#define AGENTS_BEGIN 55
#define AGENTS_END 56
#define TASK_BEGIN 57
#define TASK1 58
#define TASK2 59
#define TASK3 60

int main() {
    while (mc_Barrier(AGENTS_BEGIN) == MC_ERR_NOT_FOUND) {
        printf("sleeping...\n");
        sleep(1);
    }
    srand(time(NULL));
    if( mc_host_port == 5051) {
        printf("Agent 1: Beginning task on port %d.\n",
                mc_host_port);
        sleep( rand() % 10 );
        printf("Agent 1: Task finished.");
    } else if (mc_host_port == 5053) {
        /* Coordinate with agent 3 to perform 3 tasks. */
        while( mc_Barrier(TASK_BEGIN) == MC_ERR_NOT_FOUND ) {
            sleep(1);
        }
        printf("Agent 1: Begin Task 1.\n");
        sleep(rand()%10);
        printf("Agent 1: End task 1.\n");
        mc_Barrier(TASK1);
        printf("Agent 1: Begin Task 2.\n");
        sleep(rand()%10);
        printf("Agent 1: End task 2.\n");
        mc_Barrier(TASK2);
        printf("Agent 1: Begin Task 3.\n");
        sleep(rand()%10);
        printf("Agent 1: End task 3.\n");
        mc_Barrier(TASK3);
    } else {
        printf("Agent 1: Arrived at port %d: Nothing to do.\n",
                mc_host_port);
    }

    while(mc_Barrier(AGENTS_END) == MC_ERR_NOT_FOUND) {
        sleep(1);
    }
    return 0;
}
